import React, { useState, useContext } from 'react';
import { auth, uploadMedia, firestore } from '../services/firebase';
import { collection, addDoc, serverTimestamp } from 'firebase/firestore';
import { InspectionContext } from '../contexts/InspectionContext';
export default function SubmitReport() {
  const { assetId, photos, videos, defects, vibrationResult, rawVibrationData } = useContext(InspectionContext);
  const [loading,setLoading]=useState(false);
  const [msg,setMsg]=useState(null);
  const submit=async()=>{setLoading(true);
    try{
      const uid=auth.currentUser.uid;
      const ts=Date.now();
      const base=`artifacts/demo/users/${uid}/${assetId}/${ts}`;
      const photoUrls=await uploadMedia(`${base}/photos`,photos);
      const videoUrls=await uploadMedia(`${base}/videos`,videos);
      await addDoc(collection(firestore,`artifacts/demo/users/${uid}/inspection_reports`),{
        assetId, userId:uid, timestamp:serverTimestamp(), defects, photoUrls, videoUrls, vibrationResult, rawVibrationData:JSON.stringify(rawVibrationData)
      });
      setMsg('Report submitted successfully');
    }catch(e){console.error(e); setMsg('Submission failed');}
    setLoading(false);
  };
  return (<div className="mt-4">
    <button onClick={submit} disabled={loading} className="bg-purple-500 text-white px-4 py-2 rounded disabled:opacity-50">
      {loading?'Submitting...':'Submit Inspection Report'}
    </button>
    {msg && <p className="mt-2">{msg}</p>}
  </div>);
}



