/*
File: apps/inspection-app/src/components/MediaCapture.js
*/
import React, { useRef, useState, useContext } from 'react';
import { InspectionContext } from '../contexts/InspectionContext';
import { getCurrentPosition } from '../services/geolocation';

export default function MediaCapture() {
  const videoRef = useRef(null);
  const recorderRef = useRef(null);
  const { photos, setPhotos, videos, setVideos } = useContext(InspectionContext);

  const [stream, setStream]       = useState(null);
  const [recording, setRecording] = useState(false);
  const [error, setError]         = useState('');

  // Called on user click to wake up the camera
  const initCamera = async () => {
    try {
      // find rear camera if possible
      const devices = await navigator.mediaDevices.enumerateDevices();
      const rear = devices.find(d =>
        d.kind === 'videoinput' &&
        /back|rear|environment/gi.test(d.label)
      );

      const constraints = {
        video: rear
          ? { deviceId: { exact: rear.deviceId } }
          : { facingMode: 'environment' },
        audio: true
      };

      const s = await navigator.mediaDevices.getUserMedia(constraints);
      setStream(s);
      videoRef.current.srcObject = s;

      const recorder = new MediaRecorder(s);
      recorder.ondataavailable = async e => {
        const blob = e.data;
        let pos;
        try { pos = await getCurrentPosition(); }
        catch {
          pos = { latitude: null, longitude: null, accuracy: null, timestamp: Date.now() };
        }
        const url = URL.createObjectURL(blob);
        setVideos(v => [
          ...v,
          { blob, url, location: { latitude: pos.latitude, longitude: pos.longitude, accuracy: pos.accuracy }, timestamp: pos.timestamp }
        ]);
      };
      recorderRef.current = recorder;
    } catch (e) {
      console.error('Camera init error:', e);
      setError(e.message || 'Failed to access camera');
    }
  };

  const takePhoto = () => {
    if (!stream) return setError('Camera not initialized');
    const video = videoRef.current;
    const canvas = document.createElement('canvas');
    canvas.width  = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext('2d').drawImage(video, 0, 0);
    canvas.toBlob(async blob => {
      let pos;
      try { pos = await getCurrentPosition(); }
      catch {
        pos = { latitude: null, longitude: null, accuracy: null, timestamp: Date.now() };
      }
      const url = URL.createObjectURL(blob);
      setPhotos(p => [
        ...p,
        { blob, url, location: { latitude: pos.latitude, longitude: pos.longitude, accuracy: pos.accuracy }, timestamp: pos.timestamp }
      ]);
    }, 'image/jpeg');
  };

  const toggleRec = () => {
    if (!recorderRef.current) return setError('Camera not initialized');
    if (recording) {
      recorderRef.current.stop();
      setRecording(false);
    } else {
      recorderRef.current.start();
      setRecording(true);
    }
  };

  // UI
  return (
    <div className="mb-4">
      {!stream && (
        <button
          onClick={initCamera}
          className="bg-blue-600 text-white px-4 py-2 rounded mb-2"
        >
          Initialize Camera
        </button>
      )}
      {error && (
        <p className="text-red-600 mb-2">Error: {error}</p>
      )}
      {stream && (
        <>
          <video
            ref={videoRef}
            autoPlay
            playsInline
            muted
            className="w-full max-w-md mb-2 rounded shadow"
          />
          <div className="space-x-2 mb-2">
            <button
              onClick={takePhoto}
              className="bg-green-500 text-white px-3 py-1 rounded"
            >
              Take Photo
            </button>
            <button
              onClick={toggleRec}
              className="bg-yellow-500 text-white px-3 py-1 rounded"
            >
              {recording ? 'Stop Recording' : 'Record Video'}
            </button>
          </div>
          <div className="flex space-x-2 overflow-x-auto">
            {photos.map((p, i) => (
              <img
                key={i}
                src={p.url}
                alt={`Photo ${i+1}`}
                className="w-20 h-20 object-cover rounded"
              />
            ))}
            {videos.map((v, i) => (
              <video
                key={i}
                src={v.url}
                controls
                className="w-20 h-20 rounded"
              />
            ))}
          </div>
        </>
      )}
    </div>
  );
}
